import { MutableRefObject, useCallback, useEffect, useRef } from 'react'
import * as React from 'react';
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import StepContent from '@mui/material/StepContent';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';

import { a1Data } from './data/a1'

type Steps = {
  label: string;
  description: string;
}

const dataToSteps = (data: string[]) => {
  return data.reduce((acc, val, i) => {
    return [...acc, { label: '', description: val}]
  }, [] as Steps[])
}

export default function App() {
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = dataToSteps(a1Data)
  const ref = useRef<HTMLDivElement>()

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };
  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };
  const handleReset = () => {
    setActiveStep(0);
  };


  const handleKeyPress = useCallback(
    (event: KeyboardEvent) => {
      console.log(ref)
      ref.current?.scrollIntoView({behavior: 'smooth'})
        if (event.key === " " || event.key === 'Enter') {
          event.preventDefault()
          handleNext();
        }
        if (event.key === "Backspace") {
          handleBack();
        }
        if (event.key === "r") {
          handleReset();
        }

    },
    [handleNext]
  );

  useEffect(() => {
    window.addEventListener("keydown", handleKeyPress);
    return () => {
      window.removeEventListener("keydown", handleKeyPress);
    };
  }, [handleKeyPress]);

  //@ts-ignore
  return (
    <Box sx={{  maxWidth: '50%', margin: '0 auto', color: 'white', backgroundColor: '#333', maxHeight: '100vh', overflowY: 'auto' }}>
      <Stepper activeStep={activeStep} orientation="vertical" sx={{ ml: 2}}>
        {steps.map((step, index) => (
          <Step key={step.label} ref={index === activeStep ? ref : null} >
            <StepLabel StepIconProps={{sx: { color: 'darkred'}}}>
              {step.label}
            </StepLabel>
            <StepContent>
              <Typography>{step.description}</Typography>
              <Box sx={{ mb: 2 }}>
                <div>
                  <Button
                    variant="contained"
                    onClick={handleNext}
                    sx={{ mt: 1, mr: 1, color: 'white' }}
                  >
                    {index === steps.length - 1 ? 'Finish' : 'Continue'}
                  </Button>
                  <Button
                    disabled={index === 0}
                    onClick={handleBack}
                    sx={{ mt: 1, mr: 1, color: 'white' }}
                  >
                    Back
                  </Button>
                </div>
              </Box>
            </StepContent>
          </Step>
        ))}
      </Stepper>
    </Box>
  );
}
