import { useCallback, useEffect, useRef } from 'react'
import * as React from 'react';
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import StepContent from '@mui/material/StepContent';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';

import { a1Data } from './data/a1'

type Steps = {
  label: string;
  description: string;
}

const dataToSteps = (data: string[]) => {
  return data.reduce((acc, val, i) => {
    return [...acc, { label: String(i), description: val}]
  }, [] as Steps[])
}

export default function App() {
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = dataToSteps(a1Data)
  const ref = useRef<HTMLDivElement>()

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };
  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };
  const handleReset = () => {
    setActiveStep(0);
  };


  const handleKeyPress = useCallback(
    (event: KeyboardEvent) => {
      console.log(event)
      if (event.key === " ") {
        event.preventDefault()
        handleNext();
      }
      if (event.key === "Backspace") {
        handleBack();
      }
      if (event.key === "r") {
        handleReset();
      }

      event.current?.scrollIntoView({behavior: 'smooth', block: 'start'})

    },
    [handleNext]
  );

  useEffect(() => {
    window.addEventListener("keydown", handleKeyPress);
    return () => {
      window.removeEventListener("keydown", handleKeyPress);
    };
  }, [handleKeyPress]);

  return (
    <Box sx={{ maxWidth: 400 }}>
      <Stepper activeStep={activeStep} orientation="vertical">
        {steps.map((step, index) => (
          <Step key={step.label} ref={handleKeyPress}>
            <StepLabel>
              {step.label}
            </StepLabel>
            <StepContent>
              <Typography>{step.description}</Typography>
              <Box sx={{ mb: 2 }}>
                <div>
                  <Button
                    variant="contained"
                    onClick={handleNext}
                    sx={{ mt: 1, mr: 1 }}
                  >
                    {index === steps.length - 1 ? 'Finish' : 'Continue'}
                  </Button>
                  <Button
                    disabled={index === 0}
                    onClick={handleBack}
                    sx={{ mt: 1, mr: 1 }}
                  >
                    Back
                  </Button>
                </div>
              </Box>
            </StepContent>
          </Step>
        ))}
      </Stepper>
      {activeStep === steps.length && (
        <Paper square elevation={0} sx={{ p: 3 }}>
          <Typography>All steps completed - you&apos;re finished</Typography>
          <Button onClick={handleReset} sx={{ mt: 1, mr: 1 }}>
            Reset
          </Button>
        </Paper>
      )}
    </Box>
  );
}
